-- Insert combinado para Artículos, Publicaciones y Categorías
DO $$
DECLARE
    imagen_base64 TEXT := 'data:image/jpeg;base64,'
WITH nuevos_articulos AS (
    INSERT INTO Articulo (nombre, descripcion, precio, imagen, stock, id_estado_articulo)
    VALUES 
        ('Laptop Gaming ROG Strix', 'Laptop de alto rendimiento para gaming con procesador Intel i7, 16GB RAM, RTX 4060', 8500.00, imagen_base64, 5, 1),
        ('Mouse Inalámbrico Logitech MX Master 3', 'Mouse ergonómico inalámbrico con sensor de alta precisión, batería recargable y conectividad Bluetooth', 650.00, imagen_base64, 15, 1),
        ('Teclado Mecánico Keychron K8', 'Teclado mecánico compacto con switches Gateron Brown, iluminación RGB y conexión inalámbrica', 1200.00, imagen_base64, 8, 1),
        ('Lámpara de Escritorio LED', 'Lámpara moderna con control táctil, 3 niveles de brillo y puerto USB para carga', 350.00, imagen_base64, 20, 1),
        ('Cuaderno Universitario', 'Cuaderno de 200 hojas cuadriculadas, pasta dura, tamaño carta', 45.00, imagen_base64, 50, 1)
    RETURNING id_articulo
),
nuevas_publicaciones AS (
    INSERT INTO Publicacion (id_articulo, id_usuario, fecha_hora_entrega)
    SELECT id_articulo, 1, CURRENT_TIMESTAMP
    FROM nuevos_articulos
    RETURNING id_articulo
),
articulos_numerados AS (
    SELECT id_articulo, ROW_NUMBER() OVER (ORDER BY id_articulo) as num
    FROM nuevos_articulos
)
INSERT INTO Categoria (id_articulo, id_categoria_tipo)
SELECT 
    an.id_articulo,
    CASE 
        WHEN an.num = 1 THEN 1  -- Laptop: Tecnología + Personal
        WHEN an.num = 2 THEN 1  -- Mouse: Tecnología
        WHEN an.num = 3 THEN 1  -- Teclado: Tecnología + Hogar
        WHEN an.num = 4 THEN 5  -- Lámpara: Decoración
        WHEN an.num = 5 THEN 3  -- Cuaderno: Académico
    END as id_categoria_tipo
FROM articulos_numerados an
UNION ALL
SELECT 
    an.id_articulo,
    4  -- Personal 
FROM articulos_numerados an
WHERE an.num = 1
UNION ALL
SELECT 
    an.id_articulo,
    2  -- Hogar 
FROM articulos_numerados an
WHERE an.num = 3;